const redux = require("redux");

//this is a reducer() which will be called for manipulating the states in the store 
//this function is called by components using dispatch method 
function counterReducer(state = { counter: 0 }, action) {//also called when the store is initialised
  return {
    counter: state.counter + 1,
  };
}

//here we made the store to store states(in a state object)
const counterStore = redux.createStore(counterReducer); 

//this is a subscriber() which will be called everytime when store states changes
function counterSubscriber(){
    const latestState=counterStore.state;
    console.log(latestState);
}

//here we subscribed this function(component) to store 
counterStore.subscribe(counterSubscriber);


export default counterStore;
